CREATE DEFINER=`root`@`localhost` PROCEDURE `Proc_PagingFilterSort`(IN $where VARCHAR(255), IN $pageSize VARCHAR(10), IN $pageIndex VARCHAR(10), OUT $total VARCHAR(10), IN $sort VARCHAR(255))
    COMMENT 'Lấy dữ liệu phân trang, sắp xếp, tìm kiếm'
BEGIN
  IF $pageSize<0 THEN
    SET $pageSize = 50;
  END IF;

 IF $pageIndex<0 THEN
    SET $pageIndex = 1;
  END IF;
  
  SET @offset = ($pageIndex-1) * $pageSize;
  SET @limit = $pageSize;
   SET @defaultSort = 'CreateDate';
  SET @defaultWhere = '1 = 1';
  
  IF ISNULL($where) or Length($where) = 0 THEN
    SET $where = @defaultWhere;
  END IF;

  IF ISNULL($sort) OR LENGTH($sort)=0 THEN
  SET $sort = @defaultSort;
  END IF;

  #Lấy paging
  SET @sql = CONCAT('select * from view_product where ', $where ,' order by ', $sort, ' DESC limit ?,?');

  PREPARE stmt FROM @sql;
  EXECUTE stmt USING @offset,@limit;
  DEALLOCATE PREPARE stmt;

  #Lấy total count
  SET @count = 0;
  SET @sqlTotalCount =  Concat('select count(*)', ' from view_product where ', $where, ' into @count');
  PREPARE stmtCount FROM @sqlTotalCount;
  EXECUTE stmtCount;
  DEALLOCATE PREPARE stmtCount;
  SET $total = @count;
END ;;
